    Route::get('show-form/{id}', function($id = null)
    {
      $data = array();

      if ($id) 
      {
           if (!$show = Show::find($id))
           {
              return 'No show with that ID';
           }

           $data = array(
                 'id'     => $id,
                 'method' => 'PUT',
                 'name'   => $show->name,
                 'year'   => $show->year
            );
      } 
      else 
      {
           $data = array(
                 'id'     => '',
                 'method' => 'POST',
                 'name'   => '',
                 'year'   => ''
           );
      }
      return View::make('show-form', $data);
    });
